import axios from 'axios';

axios.defaults.xsrfHeaderName = "X-CSRFTOKEN";
axios.defaults.xsrfCookieName = "XCSRF-TOKEN";

const api = axios.create({
    baseURL: 'http://localhost:5555',
});

export default api;
